mapper.sql:

  and ba_name like concat(concat('%',#{ba_name}),'%') 


/*状态查询*/
  and ba_status=#{ba_status}

/*关于开始时间的查询*/

  and ba_datebegin  between #{ba_datebegin1} and #{ba_datebegin2}

/*关于结束时间的查询*/

  and ba_dateend  between #{ba_dateend1} and #{ba_dateend2}

mapper.select:
select * from AwardV_Evaluate bv where 1=1

ORDER BY ba_id DESC
limit #{pagestart},#{pagesize}
