mapper.select:
 select
sp_id,sp_title,sp_cityname,sp_signcode,sp_createdate
 from  Desig_Project where 1=1
 AND sp_createid =#{sp_createid,jdbcType=BIGINT}

 ORDER BY sp_id DESC
 limit #{pagestart},#{pagesize}

select * from Desig_Project dp where 1=1

ORDER BY sp_id DESC
limit #{pagestart},#{pagesize}

mapper.sql:

  and sp_no like concat(concat('%',#{sp_no}),'%') 


  and sp_title like concat(concat('%',#{sp_title}),'%')


  and sp_citycode like concat(concat('%',#{sp_citycode}),'%')


  and sp_cityname like concat(concat('%',#{sp_cityname}),'%')


  and sp_createno like concat(concat('%',#{sp_createno}),'%')


  and sp_status=#{sp_status}

/*关于建立日期时间的查询*/

  and sp_createdate  between #{sp_createdate1} and #{sp_createdate2}

/*关于申述日期时间的查询*/

  and sp_confirmdate  between #{sp_confirmdate1} and #{sp_confirmdate2}
