mapper.select:
select of_id,of_no,of_dateorder
from Sales_Order  where of_status=#{status,jdbcType=TINYINT} and ifnull(of_flagstop,0)=0


  and ifnull(of_flagfinish,0)=0


  and ifnull(of_flagfinish,0)=1





  and of_createid=#{salerid,jdbcType=BIGINT}


  and of_custid=#{designerid,jdbcType=BIGINT}

order by of_id

  limit #{limitqty,jdbcType=INTEGER}

select count(*) as tc from Sales_Order  where of_status=#{status,jdbcType=TINYINT} and ifnull(of_flagstop,0)=0
and of_createid=#{salerid,jdbcType=BIGINT}
and of_custid=#{designerid,jdbcType=BIGINT}
