mapper.select:
select ur_id,ur_no,ur_token,ur_namenick,ur_namefull,ur_phone,ur_email,ur_imageurl,ur_sex,ur_stopflag,ur_usertype
,wl_account,wl_integral,wl_deposit,ur_msgflag1,ur_msgflag2,ur_msgflag3,ur_token,ue_companyid,ue_companyname
,ue_personmajor,ur_leveltype,um_msgsumtime,ue_personjob

  ,ur_password

from SysV_UserFull
where ifnull(ur_stopflag,0)=0
and  ur_usertype=#{usertype,jdbcType=TINYINT}

  and  ur_no=#{account,jdbcType=VARCHAR}
  and  ur_password=#{password,jdbcType=VARCHAR}
  and not ISNULL(ur_password)


  and  ur_phone=#{account,jdbcType=VARCHAR}
  and  ur_password=#{password,jdbcType=VARCHAR}
  and not ISNULL(ur_password)


  and  email=#{account,jdbcType=VARCHAR}
  and  ur_password=#{password,jdbcType=VARCHAR}
  and not ISNULL(ur_password)


  and  ur_token=#{account,jdbcType=VARCHAR}
  and not ISNULL(ur_token)


  and  ur_openid_wx=#{account,jdbcType=VARCHAR}
  and not ISNULL(ur_openid_wx)


  and  ur_openid_qq=#{account,jdbcType=VARCHAR}
  and not ISNULL(ur_openid_qq)


  and  ur_openid_mini=#{account,jdbcType=VARCHAR}
  and not ISNULL(ur_openid_mini)


  and  ur_phone=#{account,jdbcType=VARCHAR}
  and not ISNULL(ur_phone)


  and  ur_token=#{account,jdbcType=VARCHAR}
  and  ur_login_imei=#{password,jdbcType=VARCHAR}
  and not ISNULL(ur_token)

limit 1

mapper.sql:

  and ur_no like concat(concat('%',#{ur_no}),'%') 


  and ur_phone like concat(concat('%',#{ur_phone}),'%')



  and ur_usertype=#{ur_usertype}



  and ur_email like concat(concat('%',#{ur_email}),'%')



  and ur_namefirst like concat(concat('%',#{ur_namefirst}),'%')



  and ifnull(ur_msgflag2,0) =#{ur_msgflag2}



  and ifnull(ur_stopflag,0) =#{ur_stopflag}

/*关于注册时间的查询*/

  and ur_register_date  between #{ur_register_date1} and #{ur_register_date2}

/*关于登陆时间的查询*/

  and ur_login_date  between #{ur_login_date1} and #{ur_login_date2}

mapper.select:
select * from SysV_UserFull sm where ur_usertype in (1,2,4)

ORDER BY ur_usertype asc ,ur_id desc
limit #{pagestart},#{pagesize}
