  and ur_usertype = #{usertype,jdbcType=INTEGER}



  and ur_no = #{ur_no,jdbcType=VARCHAR}


  and ur_phone = #{ur_phone,jdbcType=VARCHAR}


  and ur_email = #{ur_email,jdbcType=VARCHAR}


  and ur_token = #{ur_token,jdbcType=VARCHAR}

